mapper.select:
select id, name, price, stock, is_deleted, updated_at
from product
where id = #{id}

mapper.update:
update product
set is_deleted = #{product.isDeleted}
where display_product_id = #{id}
  and is_deleted = false

update product
set stock = #{stock}
where id = #{id}
  and updated_at = #{updatedAt}
